mapper.select:
SELECT 
FROM t_sys_role role_
WHERE role_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_sys_role
role_name = #{roleName,jdbcType=VARCHAR}, root_admin = #{rootAdmin,jdbcType=INTEGER}, state = #{state,jdbcType=INTEGER}, note = #{note,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
